INSERT INTO `tbl_contents` (`cId`, `cTitle`, `cDes`, `cDetail`, `cImage`, `cCreateAt`) VALUES
(1, 'เคล็ดลับและขั้นตอนการเคลม', 'สำหรับการรับมือเมื่อเกิดอุบัติ ก่อนอื่นควรตั้งสติ เช็คดูว่าทุกคนปลอดภัยหรือไม่ ', '<h1>เคล็ดลับและขั้นตอนการเคลม</h1>\r\n\r\n<p>สำหรับการรับมือเมื่อเกิดอุบัติ ก่อนอื่นควรตั้งสติ เช็คดูว่าทุกคนปลอดภัยหรือไม่ อย่าเพิ่งทำการเจรจาต่อรองใดๆ<br />\r\nจากนั้นพูดคุยกับคู่กรณี เพื่อให้ได้มาซึ่งข้อมูลต่อไปนี้</p>\r\n\r\n<p>สอบถามข้อมูลของคู่กรณี ดังนี้:</p>\r\n\r\n<ul>\r\n	<li>ป้ายทะเบียน</li>\r\n	<li>ชื่อผู้ขับขี่</li>\r\n	<li>หมายเลขใบอนุญาตขับขี่ หรือเลขบัตรประชาชน</li>\r\n	<li>ที่อยู่</li>\r\n	<li>เบอร์โทรศัพท์</li>\r\n	<li>รายละเอียดการประกันภัย</li>\r\n</ul>\r\n\r\n<p>ทั้งนี้ คุณต้องบันทึกข้อมูลสถานที่เกิดเหตุให้ชัดเจน โดยถ่ายภาพและวิดีโอ ทั้งรถและทรัพย์สินก่อนที่จะถูกโยกย้าย แต่คุณยังอุ่นใจได้แม้ในยามวิกฤติ เพราะเราการันตีไปถึงที่เกิดเหตุใน 30 นาที<br />\r\n(ในกรุงเทพฯ และอำเภอเมืองทั่วประเทศไทย) หากช้ากว่านั้น รับฟรี! บัตรเติมน้ำมันมูลค่า 1,000 บาท<br />\r\n<br />\r\n<a href="http://www.directasia.co.th/DirectAsiaThailand/media/documents/th/Motor-Insurance-Claim-Form.pdf" target="_blank">ดาวน์โหลดแบบฟอร์มแจ้งเคลม</a></p>\r\n\r\n<p><img src="http://www.directasia.co.th/DirectAsiaThailand/media/assets/images/content/claims/accident-next-steps.jpg" style="height:auto; margin:0px auto" /></p>\r\n', '1434180601.jpg', '2015-06-13 14:30:01'),
(2, 'ข้อควรปฏิบัติ เวลาเกิดเหตุการณ์ต่างๆ', 'คุณควรบันทึกข้อมูลเอาไว้...', '<h2>ข้อควรปฏิบัติ</h2>\r\n\r\n<p>เวลาเกิดเหตุการณ์ต่างๆ ขึ้น คุณควรบันทึกข้อมูลเอาไว้ และต้องรู้ว่าสิ่งใดควรหรือไม่ควรทำ<br />\r\nตามคำแนะนำดังนี้</p>\r\n\r\n<p>เมื่อมีผู้ได้รับบาดเจ็บ</p>\r\n\r\n<p>หากมีผู้บาดเจ็บ หรือเกิดอุบัติเหตุรุนแรง ไม่ควรเคลื่อนย้ายผู้บาดเจ็บหรือยานพาหนะ แต่ควรโทรแจ้ง 191 แล้วโทรหาประกัน เราจะส่งพนักงานไปช่วยคุณทันที<br />\r\n&nbsp;</p>\r\n\r\n<p><img src="http://www.directasia.co.th/DirectAsiaThailand/media/assets/images/content/claims/ambulance.png" /></p>\r\n\r\n<p><img src="http://www.directasia.co.th/DirectAsiaThailand/media/assets/images/content/claims/screw-square.png" /><br />\r\n&nbsp;</p>\r\n\r\n<p>เมื่อกระจกหน้าหรือหน้าต่างเสียหาย</p>\r\n\r\n<p>โทรหาเราที่ 02-627-7788 จะมีร้านกระจกไปซ่อมหรือเปลี่ยนกระจกให้ทันที</p>\r\n\r\n<p>เมื่อต้องการรถลาก</p>\r\n\r\n<p>หากจำเป็นต้องลากรถ ไม่ควรใช้บริการรถลากจากที่อื่น เพราะจะทำให้การเคลมล่าช้า แต่ให้ติดต่อสายด่วนอุบัติเหตุและแจ้งเคลม 02-627-7788 เราจะรีบไปช่วยคุณทันที</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img src="http://www.directasia.co.th/DirectAsiaThailand/media/assets/images/content/claims/pick-up-car.png" /></p>\r\n\r\n<p><img src="http://www.directasia.co.th/DirectAsiaThailand/media/assets/images/content/claims/car-bump.png" /><br />\r\n&nbsp;</p>\r\n\r\n<p>เมื่อต้องรายงานอุบัติเหตุ</p>\r\n\r\n<p>ทันทีที่เกิดอุบัติเหตุ มีหรือไม่มีความเสียหายใดๆ เกิดขึ้น หรือคุณไม่ได้ต้องการเคลม คุณก็ควรแจ้งประกันทันที เพราะหากไม่แจ้ง จะมีผลต่อส่วนลดเบี้ยประวัติดีของคุณ</p>\r\n\r\n<p>เมื่อต้องการซ่อมรถ</p>\r\n\r\n<p>สามารถเข้าซ่อม ในอู่รถยนต์ที่เราแนะนำ หรือ &quot;อู่ห้าง/อู่ของฉัน&quot; ทั่วประเทศ<br />\r\n&nbsp;</p>\r\n\r\n<p><img src="http://www.directasia.co.th/DirectAsiaThailand/media/assets/images/content/claims/screw-car.png" /></p>\r\n\r\n<p><img src="http://www.directasia.co.th/DirectAsiaThailand/media/assets/images/content/claims/paper-police.png" /><br />\r\n&nbsp;</p>\r\n\r\n<p>เมื่อต้องแจ้งตำรวจ</p>\r\n\r\n<p>คุณต้องแจ้งตำรวจในกรณีดังต่อไปนี้ มีผู้ได้รับบาดเจ็บ, เกิดเหตุชนแล้วหนี, หรือมีรถยนต์/ทรัพย์สินของทางราชการ, รถยนต์ของชาวต่างชาติ หรือ เกี่ยวข้องกับคนเดินถนน/ผู้ขับขี่จักรยาน</p>\r\n', '1434180882.jpg', '2015-06-13 14:34:42'),
(3, 'ทำไมเราเสนอเบี้ยประกันได้ราคาถูก', 'หลายคนคิดว่าราคาที่ถูกอาจเพราะบริการ...', '<h1>ทำไมเราเสนอเบี้ยประกันได้ราคาถูก</h1>\r\n\r\n<p>หลายคนคิดว่าราคาที่ถูกอาจเพราะบริการไม่ดีพอ หรือ ตัดความคุ้มครองบางส่วนไป<br />\r\nแต่ที่ DirectAsia.com เรากล้าการันตีคุณภาพงานบริการที่ดีกว่า ในราคาที่ดีที่สุด และในความคุ้มครองที่คุณเลือกได้เอง ไม่จำเป็นต้องจ่ายในส่วนเกินที่คุณไม่ต้องการ</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2>ออกแบบประกันภัยของคุณด้วยตัวเอง</h2>\r\n\r\n<p>ด้วยรูปแบบการทำประกันที่ยืดหยุ่น DirectAsia.com จึงให้อิสระคุณในการเลือกความคุ้มครอง ตามความจำเป็นของคุณเอง เช่น สามารถเลือกซื้อกรมธรรม์แบบระบุชื่อผู้ขับขี่ เลือกความคุ้มครองเพิ่มเติมตามที่ต้องการ และปรับวงเงินประกัน ทำให้ได้ราคาเบี้ยที่ถูกกว่า&nbsp;และไม่ต้องจ่ายค่าความคุ้มครองเผื่อผู้ขับขี่รายอื่นที่มีความเสี่ยงสูง&nbsp; เช่น มีประวัติด้านอุบัติเหตุบ่อยครั้ง</p>\r\n\r\n<p><img alt="Special Offer Icon" src="http://www.directasia.co.th/DirectAsiaThailand/media/assets/images/content/directasia/green-car.png" /></p>\r\n\r\n<p>&nbsp;</p>\r\n', '1434181518.jpg', '2015-06-13 14:45:18'),
(4, 'แผนการตลาด', 'ในมุมของการทำธุรกิจเครือข่าย...', '<p><strong>&ldquo;การตลาด&rdquo;</strong> ในมุมของการทำธุรกิจเครือข่าย คือการสร้างองค์ประกอบที่ทำให้กลุ่มเป้าหมายทางการตลาดต้องการบริโภคสินค้าของคุณ : ซึ่งเกิดจากทำงานร่วมกันของ</p>\r\n\r\n<ul>\r\n	<li>การสื่อสารคุณค่า และคุณประโยชน์ ของผลิตภัณฑ์หรือบริการไปยังลูกค้าผ่านทาง ข้อความ วิดีโอ ผ่านช่องทางต่างๆ ในการทำโฆษณา</li>\r\n	<li>การสร้างคุณค่า หรือ คุณประโยชน์ที่เหนือกว่าคู่แข่ง (Value Creation) ที่ลูกค้าสัมผัสได้</li>\r\n	<li>การสร้างความสัมพันธ์กับลูกค้า (Customer Relationship)</li>\r\n	<li>การกำหนดเป้าหมายทางการตลาด (Target market)</li>\r\n	<li>การบริการหลังการขายและการรักษาลูกค้าให้อยู่กับเราตลอดไป (Royalty Customer)</li>\r\n	<li>การสร้างสินค้าที่ตอบสนองความต้องการของลูกค้าให้มากที่สุด (Customer desire Product)</li>\r\n	<li>การตลาดเพื่อสังคม (The Societal Marketing Concept)</li>\r\n	<li>การสร้างองค์ประกอบที่เอื้ออำนวยต่อการขายให้มากที่สุด</li>\r\n</ul>\r\n\r\n<p>กระบวนการ และวิธีการนำองค์ประกอบทั้ง 8 ข้อที่ทำให้คุณสามารถสร้าง<strong> &ldquo;แผนการตลาด&rdquo;</strong> ที่ดึงดูดลูกค้า ดึงดูดผู้มุ่งหวังให้เข้า มาซื้อสินค้า ของคุณโดยที่คุณไม่ต้องไปเร่ขายสินค้าแบบเซลล์แมน</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>สิ่งสำคัญในการทำการตลาดเครือข่ายที่ดีก็คือ </strong></p>\r\n\r\n<p>คุณต้องมองการตลาดให้กว้าง เพราะสายตาของนักการตลาดนั้นจะครอบคลุมองค์ประกอบทั้ง 8 ข้อเพื่อสร้างแผนการตลาด ในการทำธุรกิจเครือข่ายที่ ใช้แผนการตลาดผลักดันให้ธุรกิจเติบโต มากกว่าการเน้นที่ทักษะเฉพาะทางอย่าง &ldquo;ทักษะการขาย&rdquo; ซึ่งเป็นเหมือนการใช้เรือเอียมจุ้น ลากจูงเรือใหญ่ซึ่งจะเคลื่อนไปได้ช้า</p>\r\n\r\n<p>แต่ถ้าคุณใช้แผนการตลาดที่สร้างองค์ประกอบสำคัญที่นำไปสู่การขายได้แล้วนั้น ก็เหมือนการใช้เรือใหญ่ติดเครื่องยนต์เจ็ตลากเรือเอียมจุ้น ซึ่งทำให้ธุรกิจของคุณเติบโตอย่างรวดเร็ว จาการมีลูกค้า และผู้มุ่งหวังใหม่ๆ เติมเข้ามาสู่ธุรกิได้โดยที่ลูกค้าเก่าๆ ในรายชื่อ ก็ยังติดตามซื้อสินค้าอย่างเหนียวแน่นนั้นเอง</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>ก่อนจากกัน ผมขอให้คุณจำไว้ว่า<strong> &ldquo;แผนการตลาด&rdquo;</strong> ที่ดีที่เหมาะสมที่ทำให้คุณสร้างเครือข่าย และขยายธุรกิจเครือข่าย ของคุณให้เติบโตในการทำนั้นก็คือ <em><strong>&ldquo;การสร้างองค์การสร้างองค์ประกอบที่ทำให้กลุ่มเป้าหมายทางการตลาดต้องการบริโภคสินค้าของคุณ&rdquo; ไม่ใช่การที่มุ่งแต่จะขายสินค้าให้ได้มากๆ อย่างเดียว</strong></em></p>\r\n\r\n<p>&nbsp;</p>\r\n', '1434181813.jpg', '2015-06-13 14:50:13'),
(5, 'รู้จักให้ เคล็ดลับความสำเร็จ ', 'คล็ดลับที่ว่าก็คือ การ “รู้จักให้” ', '<p>เคล็ดลับที่ว่าก็คือ การ <strong>&ldquo;รู้จักให้&rdquo;</strong> ซึ่งแม้ฟังดูว่าเป็นเรื่องง่ายๆ (จนบ้างครั้งเรามักจะมองข้ามไป) แต่กลับเป็นสิ่งที่ทำได้ยาก โดยเฉพาะในการทำธุรกิจเครือข่าย</p>\n\n<p>เพราะ &ldquo;การให้&rdquo; นั้นจะสวนทางกับความรู้สึก และสิ่งที่ได้รับการสอนต่อๆกันมาของนักธุรกิจเครือข่ายที่เราต้องพยายาม &ldquo;ปิดการขาย&rdquo; หรือ &ldquo;สปอนเซอร์ผู้มุ่งหวังให้ได้&rdquo; แม้กระทั่งในครั้งแรกที่ได้พบหน้ากับผู้มุ่งหวัง ถ้าปิดการขายไม่ได้ในครั้งที่หนึ่ง หรือครั้งที่สอง ก็ให้เปลี่ยนไปที่เป้าหมายต่อไปจนกว่าจะสปอนเซอร์ผู้มุ่งหวังได้</p>\n\n<p>&nbsp;</p>\n\n<p>เมื่อ ก่อนผมก็พยายามทำตามสิ่งที่ได้สอนมา เหมือนเพื่อนนักธุรกิจเครือข่ายหลายๆ คน ทั้งการ &ldquo;ปิดการขาย&rdquo; ให้ได้ทุกครั้งที่ออกไปชวนผู้มุ่งหวังทำธุรกิจ ยิ่งทำก็ยิ่งรู้สึกเหนื่อย และท้อกับการโดนปฏิเสธทุกๆ 8 ใน 10 ครั้งที่ออกไปชวนผู้มุ่งหวังทำธุรกิจด้วย<br />\n&nbsp;<br />\n&nbsp;</p>\n\n<h2><strong>ทำไม <strong>การ </strong>&ldquo;รู้จักให้&rdquo; จึงทำให้คุณกลายเป็นนักธุรกิจเครือข่ายที่มีความสุข และประสบความสำเร็จได้อย่างไร?</strong></h2>\n\n<p>&nbsp;<br />\nจนกระทั่งวันหนึ่งผมได้รู้จักกับวิธีการทำการตลาดแบบดึงดูด (Attraction Marketing) และได้อ่านหนังสือเล่มหนึ่งที่ชื่อว่า &ldquo;ยิ่งให้ยิ่งได้&rdquo; (The Go-Giver) ซึ่งเปลี่ยน ทัศนคติในการทำธุรกิจเครือข่ายของผม ที่เต็มไปด้วยความต้องปิดการขายให้ได้ถึงจะประสบความสำเร็จ มาเป็นให้คุณค่าก่อน เรื่องขายเอาไว้ที่หลัง</p>\n\n<p>&nbsp;</p>\n\n<p>โดยธรรมชาติของมนุษย์นั้นซึ่งในขณะ ที่คุณคิดคุณทำการให้ หรือช่วยเหลือผู้อื่นด้วยความเต็มใจนั้น ร่างกายจะหลั่งสาร เอ็นโดรฟิน ซึ่งเป็นสารแห่งความสุข ซึ่งทำให้สมองปลอดโปร่ง ทำให้คุณมีความสุขกับสิ่งที่ทำโดยไม่เหนื่อย และในทางของการทำธุรกิจเครือข่าย และผลที่ได้รับจากการที่คุณได้ช่วยเหลือให้ผู้มุ่งหวังให้แก้ปัญหาที่เขาแก้ ไม่ตกอยู่ หรือการให้คำแนะนำที่เป็นประโยชน์ หรือการสอนวิธี นั้นจะทำให้คุณได้&hellip;</p>\n\n<p>&nbsp;</p>\n\n<ol>\n	<li>ได้รับการยอมรับ และความเชื่อมั่นว่าคุณจะเป็นผู้ที่ช่วยให้เขาประสบความสำเร็จได้</li>\n	<li>ช่วยเพิ่มพูนทักษะในการสร้างธุรกิจ หรือในการทำการตลาดให้กับตัวคุณเองด้วย</li>\n	<li>ผลตอบแทนที่คุณจะได้รับขึ้นอยู่กับคุณค่าของสิ่งที่คุณให้ และปริมาณของคนที่คุณได้ช่วยเหลือ</li>\n	<li>ทำให้ผู้มุ่งหวังรู้และเข้าใจว่าคุณให้ความสำคัญกับเขา และรับรู้ถึงคุณค่าจากสิ่งที่ได้รับ</li>\n	<li>คุณจะได้เพื่อนใหม่ และแฟนพันธุ์แท้ ที่ติดตามข่าวสารของคุณอยู่เสมอๆ ผ่าน Social Media ผ่านสื่อออนไลน์ต่างๆ</li>\n	<li>สิ่งที่คุณให้นั้นจะกลายเป็นแม่เหล็กตัวใหญ่ ที่ดึงดูดผู้มุ่งหวัง ดึงดูดลูกค้า ให้เข้ามาหาคุณเอง</li>\n</ol>\n\n<p>&nbsp;</p>\n\n<h3><strong>สิ่งสำคัญที่สุดที่จะทำให้คุณสามารถทำธุรกิจเครือข่ายอย่างมีความสุข และประสบความสำเร็จ</strong></h3>\n\n<p>&nbsp;<br />\nที่ผมอยากจะทิ้งท้ายเอาไว้ก็คือ การกำจัดกรอบความคิดแห่งความขาดแคลน ที่ว่า &ldquo;ต้องสปอนเซอร์ผู้มุ่งหวังให้ได้ ต้องปิดการขายให้ได้ &rdquo; แล้วเริ่มต้นให้สิ่งดีๆ ที่เป็นประโยชน์กับผู้มุ่งหวัง กับลูกค้าและกับตัวคุณเอง เปลี่ยนตัวเองให้เป็นแม่เหล็กตัวใหญ่ที่ดึงดูดความสำเร็จ มากกกว่าการเป็นนักขายจอมตื้อที่น่าเบื่อครับ</p>\n', '1434182016.jpg', '2015-06-13 14:53:36'),
(6, 'แผนการจ่ายผลตอบแทนในธุรกิจเครือข่าย', ' แผนการตลาดขายตรง...', '<p><br />\r\nแผนการจ่ายผลตอบแทนในธุรกิจเครือข่ายหรือMLM(Compensation Plan) หรือที่นิยมเรียกกันว่า แผนการตลาดขายตรง ถือเป็นปัจจัยหลักที่สำคัญมากที่สุดอย่างหนึ่ง ที่ส่งผลโดยตรงต่อความสำเร็จ ความล้มเหลว การเติบโตและความยั่งยืนของบริษัท MLM โดยทั่วไป ระบบขายตรงหลายชั้น หรือระบบ MLM นั้นมีลักษณะที่สำคัญซึ่งทำให้ระบบเติบโตอย่างรวดเร็ว ก็คือ การที่ผู้จำหน่ายอิสระได้รับค่าตอบแทนจากยอดขายโดยตรงของตนเองแล้ว นอกจากนี้ยังได้รับค่าตอบแทน หรือที่เรียกว่า Override จากยอดขายของผู้จำหน่ายอิสระที่ตนเองได้แนะนำให้เข้าร่วมธุรกิจและผู้ จำหน่ายอิสระที่เป็นดาวน์ไลน์ต่อๆลงไปด้วย จุดนี้นี่เองที่เป็นการเปลี่ยนแนวความคิดจากการที่ผู้จำหน่ายอิสระจะต้อง แย่งกันขายให้แก่ลูกค้า มาเป็นการช่วยกันขายเพื่อให้ได้ผลประโยชน์ร่วมกันระหว่างอัพไลน์และดาวน์ ไลน์ และยังช่วยกันแนะนำให้ลูกค้าที่ใช้สินค้าเข้ามาร่วมธุรกิจเป็นผู้จำหน่าย อิสระต่อในสายงานของตนต่อไปอีกด้วย</p>\r\n\r\n<p><a href="http://4.bp.blogspot.com/_y2yeYonLQ30/TS5xNupI8iI/AAAAAAAAAII/0HE01f2PQiw/s1600/mlm-giffarine.jpg"><img alt="mlm,ธุรกิจเครือข่าย,กิฟฟารีน" src="http://4.bp.blogspot.com/_y2yeYonLQ30/TS5xNupI8iI/AAAAAAAAAII/0HE01f2PQiw/s200/mlm-giffarine.jpg" style="height:200px; margin:5px; width:133px" /></a></p>\r\n\r\n<p><br />\r\n<br />\r\nวัตถุประสงค์หลักของแผนการจ่ายค่าตอบแทนก็เพื่อใช้เป็นนโยบายในการจ่าย ค่าตอบแทนอย่างยุติธรรมให้แก่ผู้จำหน่ายอิสระ สมาชิกหรือบุคคลใดที่ได้ดำเนินกิจกรรมที่ทำให้เกิดยอดขาย และก่อให้เกิดการเจริญเติบโตของบริษัท MLM และการขยายเครือข่ายผู้จำหน่ายอิสระ โดยที่กิจกรรมที่ได้กล่าวแล้วนั้นจะเป็นกิจกรรมที่ก่อให้เกิดการเจริญเติบโต อย่างยั่งยืนแก่บริษัทและเครือข่ายผู้จำหน่ายอิสระอย่างสมดุลและยุติธรรม<br />\r\n<br />\r\nมีคำถามมากมายเกี่ยวกับแผนการจ่ายค่าตอบแทน ผู้คนอาจจะอยากทราบว่าแผนแบบใดเป็นแผนที่ดีที่สุด คำตอบก็คงเป็นคำตอบที่ตอบยากที่สุดเช่นกัน เพราะแต่ละแผนก็จะมีจุดเด่นจุดด้อยแตกต่างกันออกไป อย่างไรก็ดี ลักษณะหรือรูปแบบของแผนการจ่ายค่าตอบแทนที่สามารถสังเกตได้จากแผนต่างๆที่ พิจารณาแล้วว่าประสบความสำเร็จ สามารถนำมาสรุปเป็นหลักการกว้างๆที่เป็นปัจจัยหลักๆที่เป็นองค์ประกอบของแผน การจ่ายค่าตอบแทนที่ประสบความสำเร็จ เพื่อให้ท่านผู้อ่านทราบถึงหลักการในการวิเคราะห์แผนการจ่ายค่าตอบแทนที่จะ นำท่านไปสู่ความสำเร็จได้<br />\r\n<br />\r\n<strong>เป้าหมายหลักของแผนการจ่ายค่าตอบแทน&nbsp;</strong>คือการจ่ายค่าตอบ แทนให้กับการดำเนินกิจกรรมต่อไปนี้คือ 1) การขายสินค้าแก่ผู้บริโภค (Sell) 2) การสร้างเครือข่ายผู้จำหน่ายอิสระ (Recruit) 3) การสร้างผู้ฝึกอบรม (Build Trainers) 4) การสร้างนักบริหารการขาย (Build Top Sales Executives) 5) การรักษาให้ผู้จำหน่ายอิสระที่ดีให้อยู่กับบริษัทได้อย่างยั่งยืน (Keep Good Distributors)<br />\r\n<br />\r\n<strong>การจ่ายค่าตอบแทนให้กับการขายสินค้าแก่ผู้บริโภค (Sell)</strong>&nbsp;เป็นการจ่ายค่าตอบแทนแก่กระบวนการหลักในการผลักดันสินค้าจากบริษัทไปสู่ผู้ บริโภค หากไม่มีการขายสินค้าแล้ว ระบบ MLM ก็จะไม่เป็นระบบที่เหมาะสม (ควรตั้งข้อสงสัยว่าน่าจะเป็นแชร์ลูกโซ่ได้) โดยปกติกำไรจากการขายปลีกจะเป็น 25 % ของราคาขายปลีก หรือเป็น 33 % ของราคาขายส่ง หากสินค้าของท่านเป็นสินค้าที่มีสินค้าแข่งขันกันอยู่ในตลาด การตั้งราคาที่เหมาะสมก็เป็นเรื่องที่จำเป็นอย่างยิ่ง การกำหนดราคาที่สูงมากๆทำได้ในกรณีที่สินค้าของท่านเป็นสินค้าที่มีนวัตกรรม ยังไม่มีสินค้าทดแทนหรือคู่แข่งอยู่ในตลาด อย่างไรก็ดี การบังคับให้ผู้จำหน่ายอิสระต้องซื้อสินค้าเก็บไว้จำนวนมากๆ เพื่อให้ได้ผลประโยชน์จากผลต่างเพิ่มมากขึ้นนั้นทำได้ แต่ต้องสมเหตุสมผล คือบริษัทต้องไม่บังคับให้ซื้อสินค้าที่ขายไม่ออก มิฉะนั้นโอกาสที่จะทำให้บริษัทไปไม่รอดก็จะสูงไปด้วย เพราะผู้จำหน่ายอิสระ ก็ขายของไม่ออกด้วยเช่นกัน ดังนั้นเมื่อผู้จำหน่ายอิสระขายของไม่ได้ บริษัทก็อยู่ยาก ดังนั้นบริษัทจึงควรจะจ่ายค่าตอบแทนสำหรับกิจกรรมในการขายสินค้าแก่ผู้ บริโภคอย่างเหมาะสม<br />\r\n<br />\r\n<strong>การจ่ายค่าตอบแทนให้กับการสร้างเครือข่ายผู้จำหน่ายอิสระ (Recruit)</strong>&nbsp;เป็นการจ่ายค่าตอบแทนสำหรับกิจกรรมหัวใจหลักในการขยายจำนวนผู้จำหน่ายอิสระ หรือการขยายทีมงาน การจ่ายค่าตอบแทนทำได้โดยการจ่ายเป็นเปอร์เซ็นต์จากยอดขายของกลุ่มหรือยอด ทีมใต้สายงานลงไป เพราะถ้าผู้จำหน่ายอิสระสร้างทีมงานเพิ่มมากขึ้นยอดขายของกลุ่มหรือทีมงาน ใต้สายงานของเขาก็จะมากขึ้นด้วยส่งผลให้ยอดคอมมิชชั่นของผู้จำหน่ายอิสระคน นั้นมากขึ้นตามไปด้วย<br />\r\n<br />\r\nการสปอนเซอร์ การแนะนำ การสร้างทีมงานหรือการบอกต่อนั้น ในระยะเริ่ม 7 วันแรกนั้นผู้ที่สมัครเข้ามาใหม่จะรู้สึกตื่นเต้นกับโอกาสทางธุรกิจที่รอ อยู่ข้างหน้าอย่างมาก มีความกระตือรือร้นที่จะทำงานหรือหาสายงานเพิ่ม หากผู้สมัครเป็นผู้จำหน่ายอิสระใหม่สามารถสร้างทีมงานเพิ่มได้ในช่วงนี้ก็จะ ทำให้การสร้างทีมงานเติมโตอย่างมาก เพราะยังมีความรู้สึกที่ดีอยู่มาก<br />\r\n<a href="http://2.bp.blogspot.com/_y2yeYonLQ30/TS5xObjSD5I/AAAAAAAAAIM/blw8cjy3Rxg/s1600/mlm-giffarine1.jpg"><img alt="mlm,ธุรกิจเครือข่าย,กิฟฟารีน" src="http://2.bp.blogspot.com/_y2yeYonLQ30/TS5xObjSD5I/AAAAAAAAAIM/blw8cjy3Rxg/s200/mlm-giffarine1.jpg" style="height:167px; margin:5px; width:200px" /></a>หากผ่านระยะเวลาไป 1 เดือนผู้สมัครเป็นผู้จำหน่ายอิสระใหม่ยังไม่สามารถสร้างสายงานของตัวเองได้ ก็ยังคงมีความหวังอยู่บ้าง แม้จะไม่ตื่นเต้นเหมือนช่วงแรกแต่ก็ยังไม่หมดไฟ<br />\r\nหากผ่านระยะเวลาไป 2 เดือนหรือมากกว่า ผู้สมัครเป็นผู้จำหน่ายอิสระใหม่ยังไม่สามารถสร้างสายงานของตัวเองได้ ไปแนะนำใครก็ไม่มีใครสมัครเข้ามาเลย ความหวังก็คงริบหรี่เต็มที ถ้าไม่มีการกระตุ้นไฟก็คงหมด<br />\r\n<br />\r\nดังนั้นแผนการจ่ายค่าตอบแทนที่ดีควรจ่ายให้แก่การแนะนำผู้จำหน่ายอิสระ ใหม่ๆให้ได้ในระยะที่เร็วที่สุดเพื่อให้สามารถรักษาความกระตือรือร้น และความสนใจในการสร้างสายงานของผู้จำหน่ายอิสระใหม่ให้มีความหวัง จึงเป็นการจำเป็นเหลือเกินที่จะต้องจ่ายค่าตอบแทนสำหรับการแนะนะผู้แทน จำหน่ายอิสระรายใหม่ให้เร็วเมื่อเขาทำงานได้ตามเป้าหมายที่เป็นไปได้ (ไม่ยากจนเกินไป)ในระยะแรก เพื่อให้เขาเหล่านั้นสามารถความเติมโตของทีมงานของเขา นั่นก็หมายความถึงการเติมโตของบริษัทด้วยเช่นกัน<br />\r\n<br />\r\nสำหรับการจ่ายผลตอบแทนในธุรกิจเครือข่าย หรือMLM ที่จะนำเสนอในลำดับต่อไปนั้น ไม่มีความคิดเห็นส่วนตัวว่าแผนไหนดีไม่ดีอย่างไร ไว้อ่านเพื่อเป็นความรู้เวลาได้ยินใครพูดว่าเป็นแผนไหน ซึ่งแผนการจ่ายผลตอบแทนที่พบในธุรกิจเครือข่ายหรือMLM นั้นมีแบบหลักๆ คือ<br />\r\n<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - แผนเมทริกซ์ (แผนเน็ตเวิร์ก หรือยูนิเลเวล)&nbsp;<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - แผนไบนารี่<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - แผนไตรนารี่<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - แผนสแตร์สเต็ป(เบรกอะเวย์)&nbsp;<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - แมทชิ่งโบนัส&nbsp;<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - กองทุนต่างๆ&nbsp;<br />\r\n&nbsp;</p>\r\n', '1434182732.jpg', '2015-06-13 15:05:32');

